example.code:
use ContentType;

// Build the body
var userDecorator = {
    "id": {
        "type": "NUMBER",
        "required": true
    },
    "username": {
        "type": "STRING",
        "required": true
    },
    "password": {
        "type": "STRING",
        "required": true
    }
};

// Add to cache
ContentType.decorator("user", userDecorator);

// Define content type
ContentType.save("CustomPage", ["user"]);
