example.code:
use Mongo;
use System;
Mongo.connect("udm_default");

var documents = Mongo.find(
    "documents",
    {
        "contentType": "page"
    }
);

foreach(document in documents) {
    System.print(document._id);
}

use Mongo;
use System;
Mongo.connect("udm_default");

var documents = Mongo.find(
    "documents", {}, {},
    {
        // Positive for ascending order, negative for descending order.
        "_id": 1
    }
);

foreach(document in documents) {
    System.print(document._id);
}

use Mongo;
use System;
Mongo.connect("udm_default");

var documents = Mongo.find(
    "documents", {},
    {
        "contentType": false
    }
);

foreach(document in documents) {
    System.print(document._id);
}
