example.code:
use Excel;
use System;
var wb = Excel.loadWorkbook("./test.xlsx");
var sheet = Excel.loadSheet(wb, "testSheet");

var col = Excel.getColumn(sheet, 1);
foreach(cell in col) {
    System.print(cell);
}

use Excel, System;
var wb = Excel.loadWorkbook("./test.xlsx");
var sheet = Excel.loadSheet(wb, "testSheet");

var col = Excel.getColumn(sheet, "A");
foreach(cell in col) {
    System.print(cell);
}
