example.code:
use ExifTool, System;

var info = ExifTool.scanFile("D:\\temp\\myFile.png");
var json = System.toJSON(info, true);

System.print(json);
/* Outputs:
{
"filePath": "D:\\Temp\\myFile.png",
"fileType": "PNG",
"fileSize" : "4.4 kB",
...
}
*/

use ExifTool, System;

var info = ExifTool.scanFile(
"D:\\temp\\myFile.png",
{
"fileType": true
}
);
var json = System.toJSON(info, true);

System.print(json);
/* Outputs:
{
"filePath": "D:\\Temp\\myFile.png",
"fileType": "PNG"
}
*/

use ExifTool, System;

var info = ExifTool.scanFile(
"D:\\temp\\myFile.png",
{},
{
"nameConvention": "capitalWord"
}
);
var json = System.toJSON(info, true);

System.print(json);
/* Outputs:
{
"File Path": "D:\\Temp\\myFile.png",
"File Type": "PNG",
"File Size" : "4.4 kB",
...
}
*/
