db.query("INSERT INTO example (name) VALUES (?)", ["Doe"], 0, connection);

use Oracle as db;

// Connect to your database
db.connect(
    "jdbc:oracle:thin:" ::
    "scott/tiger" ::
    "@//localhost:1521/myservicename"
);

var object = {
    "firstName": "John",
    "lastName": "Doe"
};

// Insert a row. Notice that we now use a different notation for the parameters
db.query("INSERT INTO people (firstName, lastName) VALUES (:firstName, :lastName)", object);

use Oracle as db, System;

// Connect to your database
db.connect(
    "jdbc:oracle:thin:" ::
    "scott/tiger" ::
    "@//localhost:1521/myservicename"
);

// Select data from table where version contains a colon
var rows = db.query("SELECT * FROM software WHERE version = :myVersion" , {"myVersion" : "1.0:2"});

foreach(row in rows) {
    System.print("Software: " :: row.name);
}
