example.code:
use System;
use REST;
use XML;
var bodyStr = "< ?xml version=\"1.0\" encoding=\"UTF-8\"?> < test name=\"xillio\">content< /test>";
var body = XML.fromString(bodyStr);
var response = REST.put("http://whatever.com/uri", {"timeout" : 3000}, body);
System.print(response); // Show the entire response
System.print(response.body); // Show the response content
