example.code:
use Web;
use System;

var page = Web.loadPage("http://www.xillio.com/");
var links = Web.cssPath(page, "img");

if (System.typeOf(links) == "node") {
    var url = Web.xPath(links, "./@src");
    System.print(url);
} else {
    foreach(key,value in links) {
    var url = Web.xPath(value, "./@src");
    System.print(url);
    }
}
