use Web;
use System;

var page = Web.loadPage("http://www.xillio.com");
System.print(Web.getText(page));

use Web;
use System;

var page = Web.loadPage("http://www.xillio.com");

var text = "";

if(page != null) {
    var header = Web.xPath(page, "//h1");
    var text = Web.getText(header);
    System.print(text);
} else {
    System.print("No page found");
}
