example.code:
use System;
use Collection;

var list1 = [1,2,3,4,5];
var list2 = [1,[1,2,3],2,3];
System.print(Collection.reverse(list1));

// Result: [5,4,3,2,1]

System.print(Collection.reverse(list2,false));

// Result: [3,2,[1,2,3],1]

System.print(Collection.reverse(list2,true));

// Result: [3,2,[3,2,1],1]
