example.code:
use Math;

var number = 3.1;
var stringNumber = "25";
var stringText = "Some Text";
var object = { "a": 2 };
var boolValue = true;

Math.isNumber(number);          // Results true
Math.isNumber(stringNumber);    // Results true
Math.isNumber(stringText);      // Results false
Math.isNumber(object);          // Results false
Math.isNumber(boolValue);       // Results true
