use System;
use String;

System.print(String.indexOf("maxTestTest", "Test"));    // Result: 3
System.print(String.indexOf("maxTestTest", "Test", 3)); // Result: 3
System.print(String.indexOf("maxTestTest", "Test", 5)); // Result: 7
System.print(String.indexOf("maxTestTest", "foo"));     // Result: -1
